import classes from "./AvailableMeals.module.css";

import MealItem from "./MealItem/MealItem";
import axios from "axios";
import React, { useEffect, useState } from "react";

const PizzaMenu = () => {
  const [data, setData] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://restouracjaapi.azurewebsites.net/api/PizzaMenu"
      );
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };
  const deleteItem = async (id) => {
    try {
      await axios.delete(
        `https://restouracjaapi.azurewebsites.net/api/PizzaMenu/${id}`
      );
      const updatedData = data.filter((item) => item.id !== id);
      setData(updatedData);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      {data.map((item) => (
        <Card
          key={item.id}
          name={item.name}
          image={item.image}
          price={item.price}
        />
      ))}
    </div>
  );
};

const Card = ({ name, image, price }) => (
  <div className="card">
    <img src={image} alt={name} />
    <h3>{name}</h3>
    <h3>Cena: {price}</h3>
    <button className="order-button">KUP</button>
    <button className={classes.orderButton} onClick={deleteItem}>
      Usu≈Ñ
    </button>
  </div>
);

export default PizzaMenu;
