import MealItem from "./MealItem/MealItem";
import axios from "axios";
import React, { useEffect, useState } from "react";
import classes from "./AvailableMeals.module.css";

const PizzaMenu = () => {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [newId, setNewId] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newImage, setNewImage] = useState("");
  const [editItem, setEditItem] = useState(null);
  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://restouracjaapi.azurewebsites.net/api/PizzaMenu"
      );
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const addItem = async () => {
    try {
      const response = await axios.post(
        "https://restouracjaapi.azurewebsites.net/api/PizzaMenu",
        {
          id: newId,
          name: newItem,
          price: newPrice,
          Image: newImage, // Ustaw odpowiednią wartość ceny
        }
      );
      setData([...data, response.data]);
      setNewItem("");
      setNewPrice("");
      setNewImage("");
    } catch (error) {
      console.error(error);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(
        `https://restouracjaapi.azurewebsites.net/api/PizzaMenu/${id}`
      );
      const updatedData = data.filter((item) => item.id !== id);
      setData(updatedData);
    } catch (error) {
      console.error(error);
    }
  };
  const editItemName = async (id, newName) => {
    try {
      await axios.put(
        `https://restouracjaapi.azurewebsites.net/api/PizzaMenu/${id}`,
        {
          name: newName,
        }
      );
      const updatedData = data.map((item) => {
        if (item.id === id) {
          return { ...item, name: newName };
        }
        return item;
      });
      setData(updatedData);
      setEditItem(null);
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div>
      {data.map((item) => (
        <Card
          key={item.id}
          name={item.name}
          image={item.image}
          price={item.price}
          deleteItem={() => deleteItem(item.id)}
          editItemName={editItemName}
          editMode={editItem === item.id}
          setEditItem={setEditItem}
        />
      ))}
      <div class="container__text2">
        <h1 className="tytulKonsola">Konsola dodawania nowych przepisów</h1>
        <div className="caloscDodawanie">
          <div className="card-2" id="dodaj">
            <input
              type="text"
              placeholder="Podaj id przepisu"
              value={newId}
              onChange={(e) => setNewId(e.target.value)}
              className="input-styledID"
            />
            <input
              type="text"
              placeholder="tytuł przepisu"
              value={newItem}
              onChange={(e) => setNewItem(e.target.value)}
              className="input-styledPrzepis"
            />
            <input
              type="text"
              placeholder="link do obrazka"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
              className="input-styled"
            />
            <button className="dodajPrzepis" onClick={addItem}>
              Dodaj przepis
            </button>
          </div>
          <div className="opis">
            <input
              type="text"
              placeholder="opis przepisu"
              value={newPrice}
              onChange={(e) => setNewPrice(e.target.value)}
              className="input-styledOpis"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

const Card = ({ id, name, image, price, deleteItem, editItemName, editMode, setEditItem }) => {
  const [editedName, setEditedName] = useState(name);

  const handleEdit = () => {
    setEditItem(id);
  };

  const handleSave = () => {
    editItemName(id, editedName);
  };

  const handleCancel = () => {
    setEditItem(null);
    setEditedName(name);
  };

  return (
    <div className="card">
      <img src={image} alt={name} />
      {editMode ? (
        <input
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
      ) : (
        <h3>{name}</h3>
      )}
      <p>Cena: {price}</p>
      {editMode ? (
        <div>
          <button onClick={handleSave}>Zapisz</button>
          <button onClick={handleCancel}>Anuluj</button>
        </div>
      ) : (
        <div>
          <button onClick={handleEdit}>Edytuj</button>
          <button onClick={deleteItem}>Usuń</button>
        </div>
      )}
    </div>
  );}

export default PizzaMenu;
