import axios from "axios";
import React, { useEffect, useState } from "react";
import classes from "./AvailableMeals.module.css";

const PizzaMenu = () => {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [newId, setNewId] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newImage, setNewImage] = useState("");

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://restouracjaapi.azurewebsites.net/api/PizzaMenu"
      );
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const addItem = async () => {
    try {
      const response = await axios.post(
        "https://restouracjaapi.azurewebsites.net/api/PizzaMenu",
        {
          id: newId,
          name: newItem,
          price: newPrice,
          image: newImage,
        }
      );
      setData([...data, response.data]);
      setNewItem("");
      setNewPrice("");
      setNewImage("");
    } catch (error) {
      console.error(error);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(
        `https://restouracjaapi.azurewebsites.net/api/PizzaMenu/${id}`
      );
      const updatedData = data.filter((item) => item.id !== id);
      setData(updatedData);
    } catch (error) {
      console.error(error);
    }
  };

  const modifyItem = async (id, newData) => {
    try {
      const response = await axios.put(
        `https://restouracjaapi.azurewebsites.net/api/PizzaMenu/${id}`,
        newData
      );

      const updatedData = data.map((item) =>
        item.id === id ? { ...item, ...newData } : item
      );
      setData(updatedData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {data.map((item) => (
        <Card
          key={item.id}
          name={item.name}
          image={item.image}
          price={item.price}
          deleteItem={() => deleteItem(item.id)}
          modifyItem={(newData) => modifyItem(item.id, newData)}
        />
      ))}
      <div className="container__text2">{/* ... */}</div>
    </div>
  );
};

const Card = ({ name, image, price, deleteItem, modifyItem }) => {
  const [newName, setNewName] = useState("");

  const handleModify = async () => {
    try {
      if (newName.trim() !== "") {
        await modifyItem({ name: newName });
        setNewName("");
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container__text">
      <div className="card">
        <img src={image} alt={name} />
        <h1 className="tytul">{name}</h1>
        <h3>Opis: {price}</h3>
        <button className="delete-button" onClick={deleteItem}>
          Usu≈Ñ
        </button>
        <div>
          <input
            type="text"
            placeholder="Nowa nazwa"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <button className="modify-button" onClick={handleModify}>
            Modyfikuj
          </button>
        </div>
      </div>
    </div>
  );
};

export default PizzaMenu;
