import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PizzaMenu = () => {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:5043/api/PizzaMenu');
      setData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const addItem = async () => {
    try {
      const response = await axios.post('http://localhost:5043/api/PizzaMenu', {
        name: newItem,
        price: 0, // ustaw wartość ceny, jaką chcesz przekazać
      });
      setData([...data, response.data]);
      setNewItem('');
    } catch (error) {
      console.error(error);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(`http://localhost:5043/api/PizzaMenu/${id}`);
      const updatedData = data.filter((item) => item.id !== id);
      setData(updatedData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {/* Wyświetlanie danych */}
      {data.map((item) => (
        <div key={item.id}>
          <p>{item.name}</p>
          <p>Price: {item.price}</p>
          <button onClick={() => deleteItem(item.id)}>Delete</button>
        </div>
      ))}

      {/* Formularz dodawania */}
      <input
        type="text"
        value={newItem}
        onChange={(e) => setNewItem(e.target.value)}
      />
      <button onClick={addItem}>Add Item</button>
    </div>
  );
};

export default PizzaMenu;
